import React, { useState } from "react";

import Card from '../UI/Card';
import Button from "../UI/Button";
import classes from './AddUser.module.css';
import UsersList from "./UsersList";

const AddUser = (props) => {
  const [entreredUsername, setEnteredUsername] = useState('');
  const [entreredAge, setEnteredAge] = useState('');

  const addUserHandler = (event) => {
    event.preventDefault();
    if(entreredUsername.trim().length === 0 || entreredAge.trim().length === 0) {
      return;
    }
    if(+entreredAge < 1) {
      return;
    }
    console.log(entreredUsername, entreredAge);
    setEnteredUsername('');
    setEnteredAge('');
  };

  const usernameChangeHandler = (event) => {
    setEnteredUsername(event.target.value);
  };

  const ageChangeHandler = (event) => {
    setEnteredAge(event.target.value);
  };

  return (
    <Card className={classes.input}>
      <form onSubmit={addUserHandler}>
        <label htmlFor="username">Username</label>
        <input id="username" type="text" value={entreredUsername} onChange={usernameChangeHandler} />
        <label htmlFor="age">Age (Years)</label>
        <input id="age" type="number" value={entreredAge} onChange={ageChangeHandler} />
        <Button type="submit">Add User</Button>
      </form>
      <UsersList />
    </Card>
  );
};

export default AddUser;
